mapper.select:
select * from batch_pay_log

    and amount = #{amount}

    and status = #{status}
    and biz_status = #{bizStatus}
    and app_name = #{appName}
    and app_parent = #{appParent}



        and check_result_count <= #{lteq_checkResultCount}




        and check_result_time <= #{lteq_checkResultTime}




limit #{offset}, #{length}
