mapper.select:
select * from pay_log
where batch_no=#{batchNo} and detail_no=#{detailNo}
limit 0,1

mapper.update:
update pay_log
set gmt_modified = now(),
      alipay_no=#{alipayNo},biz_status=#{bizStatus},error_code=#{errorCode},error_msg=#{errorMsg},remark=#{remark}
where id=#{id}

mapper.select:
select count(0) from pay_log

    and user_id = #{userId}
    and amount = #{amount}

    and batch_no = #{batchNo}
    and detail_no = #{detailNo}

    and status = #{status}
    and biz_status = #{bizStatus}
    and app_name = #{appName}
    and app_parent = #{appParent}

select * from pay_log

    and user_id = #{userId}
    and amount = #{amount}

    and batch_no = #{batchNo}
    and detail_no = #{detailNo}

    and status = #{status}
    and biz_status = #{bizStatus}
    and app_name = #{appName}
    and app_parent = #{appParent}

limit #{offset}, #{length}
