mapper.update:
update shipping_address set gmt_modified = now(),
account_id = #{accountId}, name = #{name}, postcode = #{postcode}, province = #{province}, city = #{city}, district = #{district}, phone = #{phone}, address = #{address}, status = #{status},
where id = #{id};

mapper.select:
select * from shipping_address
where account_id = #{accountId} and status = '0' and app_name = #{appName}
limit 0, 1

select count(0) from shipping_address

    and account_id = #{accountId}
    and name = #{name}
    and postcode = #{postcode}
    and province = #{province}
    and city = #{city}
    and district = #{district}
    and phone = #{phone}
    and address = #{address}
    and id = #{id}
    and status = #{status}
    and gmt_create = #{gmtCreate}
    and gmt_modified = #{gmtModified}
    and app_name = #{appName}
    and app_parent = #{appParent}

select * from shipping_address


        and account_id = #{shippingAddress.accountId}
        and name = #{shippingAddress.name}
        and postcode = #{shippingAddress.postcode}
        and province = #{shippingAddress.province}
        and city = #{shippingAddress.city}
        and district = #{shippingAddress.district}
        and phone = #{shippingAddress.phone}
        and address = #{shippingAddress.address}
        and id = #{shippingAddress.id}
        and status = #{shippingAddress.status}
        and gmt_create = #{shippingAddress.gmtCreate}
        and gmt_modified = #{shippingAddress.gmtModified}
        and app_name = #{shippingAddress.appName}
        and app_parent = #{shippingAddress.appParent}


limit #{offset}, #{length}
