mapper.update:
update web_feedback set gmt_modified = now(),
user_id = #{userId}, content = #{content}, status = #{status},
where id = #{id};

mapper.select:
select * from web_feedback
where id = #{id}
limit 0, 1

select count(0) from web_feedback

    and user_id = #{userId}
    and content = #{content}
    and id = #{id}
    and status = #{status}
    and gmt_create = #{gmtCreate}
    and gmt_modified = #{gmtModified}
    and app_name = #{appName}
    and app_parent = #{appParent}

select * from web_feedback


        and user_id = #{webFeedback.userId}
        and content = #{webFeedback.content}
        and id = #{webFeedback.id}
        and status = #{webFeedback.status}
        and gmt_create = #{webFeedback.gmtCreate}
        and gmt_modified = #{webFeedback.gmtModified}
        and app_name = #{webFeedback.appName}
        and app_parent = #{webFeedback.appParent}


limit #{offset}, #{length}
